function testColors(t) for k,v in pairs(t) do cecho("<"..v..">TEST:"..v.." "..k.."<reset>\n") end end
function lookupHunger(h) if not h then return "Starved" end for index = 1, #GUI.Lookup.HungerBreaks-1 do local i = GUI.Lookup.HungerBreaks[index] if i <= h and h < GUI.Lookup.HungerBreaks[index+1] then return GUI.Lookup.Hunger[i] elseif h == 100 then return GUI.Lookup.Hunger[h] end end end
function lookupHungerColor(h) local hungerWord = lookupHunger(h) local colorWord = GUI.Lookup.HungerColors[hungerWord] or "" --echo(colorWord.." "..h.."\n") return colorWord end
function lookupCapacityColor(c) if c > 0 and c <= 25 then return "lime_green" elseif c < 50 then return "yellow_green" elseif c < 75 then return "yellow" elseif c < 85 then return "dark_orange" else return "red" end end
